QuestClass.Code:
// I am using the sample of how to use Zeppelin/Ship

/* Quest settings:
    * Can condition: "return Usefuls.ContinentId == (int) ContinentId.Kalimdor;"
    * Is complete condition: "return Usefuls.ContinentId == (int) ContinentId.Northrend && !ObjectManager.Me.InTransport;"
    * Not required in quest log: "True"
    * Quest type: "OverridePulseCSharpCode"
*/

// You can get zeppelin/ship/player positions and entry ID in tab "Tools" > "Development Tools" > "Dump all informations" (or "Memory information").

// Settings:
var shipEntryId = 20808; // Zeppelin/Ship EntryId

// From
var fromShipWaitPosition = new Vector3(-14277.75, 582.8661, -0.02416913); // Position where Zeppelin/Ship waits players (from)
var fromPlayerWaitPosition = new Vector3(-14280.73, 567.314, 6.806983); // Position where the player waits Zeppelin/Ship (from)
var fromPlayerInShipPosition = new Vector3(-14271.17, 583.8696, 6.12158); // Position where the player waits in the Zeppelin/Ship (from)

// To
var toShipWaitPosition = new Vector3(-1005.613, -3841.648, 0.01389024); // Position where Zeppelin/Ship waits players (to)
var toPlayerLeavePosition = new Vector3(-996.8629, -3825.394, 5.477277); // Position to go out the Zeppelin/Ship (to)

// Change WRobot settings:
wManager.wManagerSetting.CurrentSetting.CloseIfPlayerTeleported = false;

// Code:
if (!Conditions.InGameAndConnectedAndProductStartedNotInPause)
    return true;

if (Usefuls.ContinentId == (int)ContinentId.Azeroth)
{
    if (!ObjectManager.Me.InTransport)
    {
        if (GoToTask.ToPosition(fromPlayerWaitPosition))
        {
            var ship = ObjectManager.GetWoWGameObjectByEntry(shipEntryId).OrderBy(o => o.GetDistance).FirstOrDefault();
            if (ship != null && ship.Position.DistanceTo(fromShipWaitPosition) < 1)
            {
                GoToTask.ToPosition(fromPlayerInShipPosition);
            }
        }
    }
}
else if (Usefuls.ContinentId == (int)ContinentId.Kalimdor)
{
    if (ObjectManager.Me.InTransport)
    {
        var ship = ObjectManager.GetWoWGameObjectByEntry(shipEntryId).OrderBy(o => o.GetDistance).FirstOrDefault();
        if (ship != null && ship.Position.DistanceTo(toShipWaitPosition) < 1)
        {
            GoToTask.ToPosition(toPlayerLeavePosition);
        }
    }
}
return true;

